const Unit = ({ unitNumber, title, description, chapters }) => {
  return (
    <div className="unit1">
      <div className="headsection">
        <center>
          <h1>Unit {unitNumber}</h1>
          <p>{description}</p>
        </center>
      </div>
      <div className="bodysection">
        <Link to="/Option">
        {chapters.map((chapter, index) => (
          <div key={index} className="chapter1">
            <img src={chapter.image} alt={`Chapter ${index + 1}`} />
            <h2>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{chapter.title}</h2>
          </div>
          
        ))}</Link>
      </div>
    </div>
  );
};

